# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.11.0"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/homebrew-ace-tools/formula/ace-dt@sha256:9df21d8f9db1f8fd9fd6803fda5527f6ce38acf3724233af8daa80f515be993e",
          using: BlobDownloadStrategy
      sha256 "9df21d8f9db1f8fd9fd6803fda5527f6ce38acf3724233af8daa80f515be993e"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/homebrew-ace-tools/formula/ace-dt@sha256:804ca8a97159f29aa93c6ea2894d5a93c169389d690b8b113fcc38cc6650ee8b",
          using: BlobDownloadStrategy
      sha256 "804ca8a97159f29aa93c6ea2894d5a93c169389d690b8b113fcc38cc6650ee8b"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/homebrew-ace-tools/formula/ace-dt@sha256:e25b1572b5c642a9b62f6bdd94938641e56b137929183f9fd2d3a8ba0ad8f4fd",
          using: BlobDownloadStrategy
      sha256 "e25b1572b5c642a9b62f6bdd94938641e56b137929183f9fd2d3a8ba0ad8f4fd"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/homebrew-ace-tools/formula/ace-dt@sha256:17e32532ce774b23671f3217b385773e7b1c7795099e0722be3e41b7f5bcd431",
          using: BlobDownloadStrategy
      sha256 "17e32532ce774b23671f3217b385773e7b1c7795099e0722be3e41b7f5bcd431"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
