# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.15.4"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:5cd3a397f4943ee9cd205cc3a909fa9c07f7cabb9e1651b9ae8bd0b5d6ee7201",
          using: BlobDownloadStrategy
      sha256 "5cd3a397f4943ee9cd205cc3a909fa9c07f7cabb9e1651b9ae8bd0b5d6ee7201"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:0eb96014895b0469f043d9c673c0b9277bd928cd34fe87d0aa467b6f045f6c65",
          using: BlobDownloadStrategy
      sha256 "0eb96014895b0469f043d9c673c0b9277bd928cd34fe87d0aa467b6f045f6c65"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:c72f7c9a1b366a8ceaccf0ace5e8b9fe6251991d3c391fe2806ee02e8cfc0d46",
          using: BlobDownloadStrategy
      sha256 "c72f7c9a1b366a8ceaccf0ace5e8b9fe6251991d3c391fe2806ee02e8cfc0d46"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:e456cab1b4cb669ec3be322a96020f60262e9dc471d826c3b01ec8cf080ee28a",
          using: BlobDownloadStrategy
      sha256 "e456cab1b4cb669ec3be322a96020f60262e9dc471d826c3b01ec8cf080ee28a"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
