# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.15.2"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:33097b59c7f3affb05bb52a730e16bff941d3e189d2824ea7420427082672cf7",
          using: BlobDownloadStrategy
      sha256 "33097b59c7f3affb05bb52a730e16bff941d3e189d2824ea7420427082672cf7"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:01f96ed0efba8bb001593c863bd98a97a6f38721a2829a9f588f1e4f2d463be4",
          using: BlobDownloadStrategy
      sha256 "01f96ed0efba8bb001593c863bd98a97a6f38721a2829a9f588f1e4f2d463be4"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:adc6efb07778a4e68268ab28fe1715fd3997b56de9efdd111fb71a1ae5f22535",
          using: BlobDownloadStrategy
      sha256 "adc6efb07778a4e68268ab28fe1715fd3997b56de9efdd111fb71a1ae5f22535"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:ee631ee07e51f73ff21068908c8619bacfbf469d27723af8734073030004ef5b",
          using: BlobDownloadStrategy
      sha256 "ee631ee07e51f73ff21068908c8619bacfbf469d27723af8734073030004ef5b"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
