# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.15.5"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:b6c87dd024f235bb227c40c9bb825d26878cd45ea06cd9a3bd0ca82333ce5016",
          using: BlobDownloadStrategy
      sha256 "b6c87dd024f235bb227c40c9bb825d26878cd45ea06cd9a3bd0ca82333ce5016"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:4458a22e0e9197882a892fba4b30e751a6f4d8ff480681af5056c29ee37d8241",
          using: BlobDownloadStrategy
      sha256 "4458a22e0e9197882a892fba4b30e751a6f4d8ff480681af5056c29ee37d8241"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:8bdf3adf52233fc7016008086b6d6c4ea00d7ba9f9a7c5d1d6d92b868aea950c",
          using: BlobDownloadStrategy
      sha256 "8bdf3adf52233fc7016008086b6d6c4ea00d7ba9f9a7c5d1d6d92b868aea950c"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:8f7e318a3f6e7ef116b344c93ecb6e50dcdfc41f748db81eb0ab52c4d23db9c6",
          using: BlobDownloadStrategy
      sha256 "8f7e318a3f6e7ef116b344c93ecb6e50dcdfc41f748db81eb0ab52c4d23db9c6"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
