# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.12.1"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:83930d76490d485d4781a6dbe9c44dc0d62b632881cfbe8690d8001b8b48a544",
          using: BlobDownloadStrategy
      sha256 "83930d76490d485d4781a6dbe9c44dc0d62b632881cfbe8690d8001b8b48a544"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:0464eb674f7ae6a516862f088f3e5a5920809bbf01ca78a6d1d2da25779e5826",
          using: BlobDownloadStrategy
      sha256 "0464eb674f7ae6a516862f088f3e5a5920809bbf01ca78a6d1d2da25779e5826"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:f806faf3455c895cb761b900c28f3910a8dbf78e8a9c68abaa014a3e7dc44e7f",
          using: BlobDownloadStrategy
      sha256 "f806faf3455c895cb761b900c28f3910a8dbf78e8a9c68abaa014a3e7dc44e7f"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:8212ba5f3c21a5ecb0dcb25e18d85953465ac435069d58f65adbd175bfb9718d",
          using: BlobDownloadStrategy
      sha256 "8212ba5f3c21a5ecb0dcb25e18d85953465ac435069d58f65adbd175bfb9718d"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
