# frozen_string_literal: true

require_relative "../lib/registry_download"

class AceDt < Formula
  desc "ACE Data Tool"
  homepage "https://git.act3-ace.com/ace/data/tool"
  version "1.13.2"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:f9538e10da0fcc63a41634ef0c83ca1206af2b7de5fab79979139ecc14dc2036",
          using: BlobDownloadStrategy
      sha256 "f9538e10da0fcc63a41634ef0c83ca1206af2b7de5fab79979139ecc14dc2036"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:8f9204bb644a88474b22ccb1a28cac05b7f8cbeedc57ef91dcbd6179072218bb",
          using: BlobDownloadStrategy
      sha256 "8f9204bb644a88474b22ccb1a28cac05b7f8cbeedc57ef91dcbd6179072218bb"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:4c0ce2fd79ce7445aa6b1d5162b5b183d5378873dac469cecabb63bdabd42b0c",
          using: BlobDownloadStrategy
      sha256 "4c0ce2fd79ce7445aa6b1d5162b5b183d5378873dac469cecabb63bdabd42b0c"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/data/tool/releases/ace-dt@sha256:4f933f1b26108449735ca80709530bfd9b95d731613aee7282d8c3cfb1d9348c",
          using: BlobDownloadStrategy
      sha256 "4f933f1b26108449735ca80709530bfd9b95d731613aee7282d8c3cfb1d9348c"
    end
  end

  def install
    bin.install "ace-dt"
    generate_completions_from_executable(bin/"ace-dt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"ace-dt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"ace-dt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/config.dt.act3-ace.io.schema.json": [
            "ace-dt-config.yaml",
            "ace/dt/config.yaml"
          ],
          "file://#{pkgetc}/data.act3-ace.io.schema.json": "entry.yaml"
        }

      Add the following to VS Code's settings.json file to enable JSON file validation:
        "json.schemas": [
          {
            "fileMatch": [
              "entry.json"
            ],
            "url": "file://#{pkgetc}/data.act3-ace.io.schema.json"
          }
        ]

      Check out the quick start guide to get started with ace-dt:
        ace-dt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/ace-dt", "version"
  end
end
