# frozen_string_literal: true

require_relative "../lib/registry_download"

class Act3Pt < Formula
  desc "ACT3 Project Tool - ACT3's project management toolbox"
  homepage "https://git.act3-ace.com/devsecops/act3-pt"
  version "2.1.1"

  depends_on "glab"
  depends_on "typst"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:1c666fef8ce1565b8e718e157c255634e10569b0405beb831c16054e8250280a",
          using: BlobDownloadStrategy
      sha256 "1c666fef8ce1565b8e718e157c255634e10569b0405beb831c16054e8250280a"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:7bb93112dc21402776ac55d24e30aab87899a3de33731cd60563c502ee5179cf",
          using: BlobDownloadStrategy
      sha256 "7bb93112dc21402776ac55d24e30aab87899a3de33731cd60563c502ee5179cf"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:11145b57121b3ad26fa75d7081cab33883790a1d78d904e6bd234ff2226f8a8d",
          using: BlobDownloadStrategy
      sha256 "11145b57121b3ad26fa75d7081cab33883790a1d78d904e6bd234ff2226f8a8d"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:a0ef1ff7f211c437314b48dc70f91a265b2ab84d67cd5ba757e9487c83642322",
          using: BlobDownloadStrategy
      sha256 "a0ef1ff7f211c437314b48dc70f91a265b2ab84d67cd5ba757e9487c83642322"
    end
  end

  conflicts_with "act3-pt-beta", because: "act3-pt and act3-pt-beta install conflicting executables"

  def install
    bin.install "act3-pt"
    generate_completions_from_executable(bin/"act3-pt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"act3-pt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"act3-pt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/project.act3-ace.io.schema.json": [
            ".project.yaml",
            ".act3-pt.yaml",
            ".blueprint.yaml",
            ".act3-template.yaml",
            ".blueprintcatalog.yaml",
            "catalog.yaml"
          ],
          "file://#{pkgetc}/pt.act3-ace.io.schema.json": [
            "act3-pt-config.yaml",
            "act3/pt/config.yaml"
          ]
        }

      Check out the quick start guide to get started with act3-pt:
        act3-pt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/act3-pt", "version"
  end
end
