# frozen_string_literal: true

require_relative "../lib/registry_download"

class Act3Pt < Formula
  desc "ACT3 Project Tool - ACT3's project management toolbox"
  homepage "https://git.act3-ace.com/devsecops/act3-pt"
  version "2.1.6"

  depends_on "glab"
  depends_on "typst"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:f5a0b7607431544be68b691b40a92a40e183112f38c5d30c8c6cfa31366f7e4d",
          using: BlobDownloadStrategy
      sha256 "f5a0b7607431544be68b691b40a92a40e183112f38c5d30c8c6cfa31366f7e4d"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:276db600351d1fbb9eedcb2d81fd1f9ba37f4fbc4ba2092df1e1028675954ff5",
          using: BlobDownloadStrategy
      sha256 "276db600351d1fbb9eedcb2d81fd1f9ba37f4fbc4ba2092df1e1028675954ff5"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:dc27025e26dd881b1bd6247e3cb6d16366638e2ecee6987cfb50e3e1cde1545b",
          using: BlobDownloadStrategy
      sha256 "dc27025e26dd881b1bd6247e3cb6d16366638e2ecee6987cfb50e3e1cde1545b"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:fd609c5445903030a020d13d4252bcaeae8c3c9174d1fc55f797fe99c6fba088",
          using: BlobDownloadStrategy
      sha256 "fd609c5445903030a020d13d4252bcaeae8c3c9174d1fc55f797fe99c6fba088"
    end
  end

  def install
    bin.install "act3-pt"
    generate_completions_from_executable(bin/"act3-pt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"act3-pt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"act3-pt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/project.act3-ace.io.schema.json": [
            ".project.yaml",
            ".act3-pt.yaml",
            ".blueprint.yaml",
            ".act3-template.yaml",
            ".blueprintcatalog.yaml",
            "catalog.yaml"
          ],
          "file://#{pkgetc}/pt.act3-ace.io.schema.json": [
            "act3-pt-config.yaml",
            "act3/pt/config.yaml"
          ]
        }

      Check out the quick start guide to get started with act3-pt:
        act3-pt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/act3-pt", "version"
  end
end
