# frozen_string_literal: true

require_relative "../lib/registry_download"

class Act3Pt < Formula
  desc "ACT3 Project Tool - ACT3's project management toolbox"
  homepage "https://git.act3-ace.com/devsecops/act3-pt"
  version "2.1.8"

  depends_on "glab"
  depends_on "typst"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:3bb11d580d7d83413694baef3cc423ac76a81b4a1ba7001f5583155b2995a51f",
          using: BlobDownloadStrategy
      sha256 "3bb11d580d7d83413694baef3cc423ac76a81b4a1ba7001f5583155b2995a51f"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:533dc22ffc46cab6cf2c0f32017287d19fa59a0da1c8e1c94160c74be7e6d46b",
          using: BlobDownloadStrategy
      sha256 "533dc22ffc46cab6cf2c0f32017287d19fa59a0da1c8e1c94160c74be7e6d46b"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:1170f9e642551adc329a37fd5d12ec092e3587cb553f606001692358180d4f08",
          using: BlobDownloadStrategy
      sha256 "1170f9e642551adc329a37fd5d12ec092e3587cb553f606001692358180d4f08"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/devsecops/act3-pt/releases/act3-pt@sha256:a4bb996fffc6439de4fc4402073895e2c32457460d26b9f2eef2ceb92eabb87a",
          using: BlobDownloadStrategy
      sha256 "a4bb996fffc6439de4fc4402073895e2c32457460d26b9f2eef2ceb92eabb87a"
    end
  end

  def install
    bin.install "act3-pt"
    generate_completions_from_executable(bin/"act3-pt", "completion")

    # Generate manpages
    mkdir "man" do
      system bin/"act3-pt", "gendocs", "man", "."
      man1.install Dir["*.1"]
      man5.install Dir["*.5"]
    end

    # Generate JSON Schema definitions
    # Use pkgetc here so path doesn't change over version numbers
    # Cannot use symlink for this because VS Code cannot follow symlinks for schema files
    mkdir pkgetc do
      system bin/"act3-pt", "genschema", "."
    end
  end

  def caveats
    <<~EOS
      Add the following to VS Code's settings.json file to enable YAML file validation:
        "yaml.schemas": {
          "file://#{pkgetc}/project.act3-ace.io.schema.json": [
            ".project.yaml",
            ".act3-pt.yaml",
            ".blueprint.yaml",
            ".act3-template.yaml",
            ".blueprintcatalog.yaml",
            "catalog.yaml"
          ],
          "file://#{pkgetc}/pt.act3-ace.io.schema.json": [
            "act3-pt-config.yaml",
            "act3/pt/config.yaml"
          ]
        }

      Check out the quick start guide to get started with act3-pt:
        act3-pt info quick-start-guide
    EOS
  end

  test do
    system "#{bin}/act3-pt", "version"
  end
end
