# frozen_string_literal: true

require_relative "../lib/registry_download"

class AsceHubCli < Formula
  desc "CLI for interacting with ACE Hub"
  homepage "https://git.act3-ace.com/ace/hub/cli"
  version "0.2.1"

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_macos do
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/hub/cli/releases/hub@sha256:85186b3042528d21821079282be40010b7e3410b0c37aa6707c916c0b10d0364",
          using: BlobDownloadStrategy
      sha256 "85186b3042528d21821079282be40010b7e3410b0c37aa6707c916c0b10d0364"
    end
    if Hardware::CPU.arm?
      url "reg.git.act3-ace.com/ace/hub/cli/releases/hub@sha256:04bc822132f92c0a02cae47ec1d27bb4f88a12a5cfa65aeac2101dd76d870094",
          using: BlobDownloadStrategy
      sha256 "04bc822132f92c0a02cae47ec1d27bb4f88a12a5cfa65aeac2101dd76d870094"
    end
  end

  # Generated by https://git.act3-ace.com/ace/homebrew-ace-tools/-/blob/master/bin/formulize.sh
  on_linux do
    if Hardware::CPU.arm? && Hardware::CPU.is_64_bit?
      url "reg.git.act3-ace.com/ace/hub/cli/releases/hub@sha256:bb6af2379e5f7369e5f7474bbec85fcf491d216eac378e71827a0a41a24dc35a",
          using: BlobDownloadStrategy
      sha256 "bb6af2379e5f7369e5f7474bbec85fcf491d216eac378e71827a0a41a24dc35a"
    end
    if Hardware::CPU.intel?
      url "reg.git.act3-ace.com/ace/hub/cli/releases/hub@sha256:e9169ce5ea6de6144c10e6d6de3f1fe31b02330983917d068c25b22036aa3ebb",
          using: BlobDownloadStrategy
      sha256 "e9169ce5ea6de6144c10e6d6de3f1fe31b02330983917d068c25b22036aa3ebb"
    end
  end

  def install
    bin.install "hub"

    with_homebrew_path do
      # Completions from downstream tools are sourced by asce,
      # so they must be discoverable on the PATH
      generate_completions_from_executable(bin/"hub", "completion")
    end

    # Generate manpages
    mkdir "man" do
      system bin/"hub", "gendocs", "man", "."
      man1.install Dir["*.1"]
      # man5.install Dir["*.5"]
    end
  end

  # test do
  #   system "#{bin}/hub", "version"
  # end
end
